function makeDiff(server, local) {
    var excludedProperties = {"_id":true, "_rev":true, "type":true};
    var list = this.get("PropertyList");

    this.newData = server;
    this.userStory = local;
    this.allDiffs = {};
    this.finalVersion = Object.clone(server); // defaults to accept

    list.updateList();

    for (property in this.newData){
        if(this.newData.hasOwnProperty(property) &&
            (typeof this.newData[property]!=="function") &&
            ! excludedProperties[property] ){
                if(this.hasChanged(property)){
                    property+=" (changed)";
                }
                list.addItem(property);
        }
    }
    list.selectAt(1);
}

