function diffProperty(property) {
        module('apps.DiffMatchPatch').load(true);
	var dmp = new diff_match_patch();
	var diffs;
	diffs = dmp.diff_main(
            this.userStory.serializePropertyAsString(property),
            this.userStory.serializePropertyAsString(property,this.newData)

        );
	dmp.diff_cleanupSemantic(diffs);
	var filteredDiffs  = []
	for(var i=0; i<diffs.length; i++) {
		var s = diffs[i][1];
		var lines = s.split(/\n/);
		// between change
		if (diffs[i-1] && diffs[i+1] && diffs[i][0] == 0 &&  lines.length > 6) {
			var beginS= lines.slice(0, 2).join("\n");
			var endS = lines.slice(lines.length - 3, lines.length).join("\n");	
			filteredDiffs.push([0, beginS + "\n...\n" + endS])
			continue;
		}

		// before change
		if (!diffs[i-1] && diffs[i+1] && diffs[i][0] == 0 &&  lines.length > 3) {
			var newS = lines.slice(lines.length - 3, lines.length).join("\n");
			filteredDiffs.push([0,"... " + newS ])
			continue;
		}
		// after change
		if (diffs[i-1] && !diffs[i+1] && diffs[i][0] == 0  &&  lines.length > 3) {
			var newS = lines.slice(0, 2).join("\n");
			filteredDiffs.push([0, newS + "... "])
			continue;
		}
		filteredDiffs.push(diffs[i])
	}
        return filteredDiffs;
}

