function renderFunction(listItem) {
        if (!listItem) listItem = {isListItem: true, string: 'invalid list item: ' + listItem};
        if (listItem.morph) return listItem.morph;
        var string = listItem.string || String(listItem);
        var listItemMorph = new lively.morphic.Text(lively.rect(0,0, 800,23), string);
        listItemMorph.item = listItem;
        listItem.emphasizeRanges && listItemMorph.emphasizeRanges(listItem.emphasizeRanges);
        listItemMorph.applyStyle({
            allowInput: false,
            borderColor: Color.rgb(204,204,204),
            borderWidth: 1,
            fill: null,
            fixedHeight: true,
            fixedWidth: false,
            overflow: "hidden",
            whiteSpaceHandling: 'pre'
        });
        return listItemMorph;
}

