function updateSessions() {
    var self = this;
    lively.net.tools.Lively2Lively.withSessionsAndDeltaDo(
        self.activeSessions, 
        function(err, activeS, newS, discS) {
            if (self.activeSessions !== undefined)
                newS.forEach(function(ea) {
                    self.addNewlyConnectedSessionEvent(ea)});
            activeS.forEach(function(ea) {
                self.updateSession(ea)});
            discS.forEach(function(ea) {
                self.addDisconnectedSessionEvent(ea)});

            self.activeSessions = activeS;
        });
}

