function updateDBObjects() {
    var self = this;
    lively.net.tools.Lively2Lively.withWikiRecordsDo(this.lastUpdate, function(err, dbRecords) {
        if (err) {
            // Just try again next tick.
            show("Retrieving Modified Objects from database failed.");
            return;
        }
        dbRecords.forEach(function(dbRec) {
            self.addObjectGraphUpdate(dbRec);
        });
        self.displayList();
        self.lastUpdate = new Date();
    });
}

