function addObjectGraphUpdate(dbRec){
    var listItem = {
        value: dbRec,
        author: dbRec.author,
        time: new Date(dbRec.date),
        world: dbRec.path,
        login: false,
    }
    var hrs = listItem.time.getHours(),
        mts = listItem.time.getMinutes(),
        timeStr = (hrs < 10 ? "0" + hrs: hrs) 
                + ':' + (mts < 10 ? "0"+ mts : mts) + " ";

    if (dbRec.path.startsWith("PartsBin")) {
        var name = this.getWorldName(dbRec.path),
            path = dbRec.path.split("/");
        path.shift(); // loose the PartsBin part
        listItem.string = timeStr + "pb: " 
            + dbRec.author 
            + (dbRec.change == "contentChange" ? 
                            " updated PartsBin item " + name : 
                            " added " + name + " to the PartsBin")
            + "(" + path.join(", ") + ")";

        var nameStart = 6 + 4 + dbRec.author.length + (dbRec.change == "contentChange" ? 23: 7);
        listItem.emphasizeRanges = [
            [6 + 0, 6 + 2, {color: Color.green}],
            [6 + 4, 6 + 4 + dbRec.author.length, {fontWeight: "bold"}],
            [nameStart, nameStart + name.length, {fontWeight: "bold"}]];
    }
    if (!listItem.string){
        listItem.string = timeStr + "" + dbRec.author + " saved " + dbRec.path;
        listItem.emphasizeRanges = [
            [6, 6 + dbRec.author.length, {fontWeight: "bold"}],
            [dbRec.author.length + 7, listItem.string.length, {fontWeight: "bold"}]];
    }
    return this.addEvent(listItem);
}

