function updateSession(aSession){
    // there are similar, prior events
    if(aSession.lastActivity === undefined) 
        return;
    var time = new Date(aSession.lastActivity),
        otherEvent;
    if (otherEvent = this.eventList.detect(function(otherEvent, i) {
        return otherEvent.world == aSession.worldURL
            && otherEvent.author == aSession.user
            && otherEvent.value.id == aSession.id
            && otherEvent.change == "changed"
    })) {
        otherEvent.time = time; // coordinate with sorting -> addEvent...
        this.setSessionEventString(otherEvent, aSession, "changed", time);
        return otherEvent;
    } else {
        // add line for changes
        var event = {
                value: aSession,
                author: aSession.user,
                world: aSession.worldURL,
                time: time, 
                login: false,
                change: "changed"
        };
        this.setSessionEventString(event, aSession, "changed", time),
        this.addEvent(event)
    }

}

