function displayList() {
    // Sorting
    this.eventList.sort(function(a, b) { return a.time.getTime() - b.time.getTime()});

    // Filtering
    var filter = this.get("FilterInput").getTextString(),
        listMorph = this.get("EventList"),
        list;
    if (filter != "") {
        var regExp = new RegExp(filter, "im");
        list = (this.eventList.select(function(ea) { 
            return ea.string.match(regExp);
        }));
    } else {
        list = this.eventList;
    }
    if(this.get("ignoreChanges").isChecked()){
        list = list.select(function(ea) {return ea.change != "changed"})
    }
    var scrollToBottom;
    try{ 
        scrollToBottom = !!(listMorph.getMaxScrollExtent().y 
                                <= listMorph.getScroll()[1])
    } catch(e) { 
        scrollToBottom = false;
    };

    listMorph.setList(list);
    scrollToBottom && listMorph.scrollToBottom();

    // var id = listMorph.selection && listMorph.selection.id;
    // var prevSel  = listMorph.itemList.detect(function(item) { return item.value.id === id; })
    // listMorph.setSelection(prevSel);
}

