function addNewlyConnectedSessionEvent(aSession){
    var time, change, loggedIn;

    // if either the timeOf* fields are missing, or the timeOf* fields are available, and registration is more than 5s later than creation
    if (undefined === aSession.timeOfRegistration || (aSession.timeOfRegistration && aSession.timeOfCreation && aSession.timeOfRegistration - aSession.timeOfCreation > 5000)){
        // asume that the user reconnected
        time = new Date(aSession.timeOfRegistration || aSession.lastActivity);
        change = "reconnected from";
        loggedIn = false;
    } else {
        time = new Date(aSession.timeOfCreation);
        change = "opened world";
        loggedIn = !!aSession.timeOfCreation
    }

    var event = {
            value: aSession,
            author: aSession.user,
            world: aSession.worldURL,
            time: time, 
            login: loggedIn,
            change: change,
    };
    this.setSessionEventString(event, aSession, change, time),

    this.addEvent(event);
}

