function setSessionEventString(anEvent, aSession, change, time) {
    if(aSession){
        var prefix    = "",
            hrs = time.getHours(),
            mts = time.getMinutes(),
            timeStr   = (hrs < 10 ? "0" + hrs: hrs) 
                        + ':' + (mts < 10 ? "0"+ mts : mts) + " ",
            user      = aSession.user + " ",
            action    = change + " ",
            worldName = this.getWorldName(aSession.worldURL) + " ",
            worldURL  = "(" + aSession.worldURL + ")";
        anEvent.string = prefix + timeStr + user + action
                        + worldName + worldURL;

        var lens = [prefix.length, timeStr.length, user.length, 
                    action.length, worldName.length, worldURL.length];
        for(var i = 1; i < lens.length; i++) lens[i] += lens[i - 1];

        anEvent.emphasizeRanges = [
                [lens[1], lens[2], {fontWeight: "bold"}],  //user
                [lens[3], lens[4], {fontWeight: "bold"}]]; //world
    } else {
        anEvent.string = "Trying to represent undefined as session string...";
        anEvent.emphasizeRanges = [];
    }
}
