212.source:
function checkForChanges() {
    // this ticking script keeps views sync'd with the DB
    var obj = this.asModel();
    var dbObj = this.dbRetrieveModel();

    if (this.equalObjects(obj, this.latestModel)) {
        // nothing has changed in the view (morph)
        // so check for a change in the database
        if (this.equalObjects(dbObj, this.latestModel)) return;
        // The database model has changed, so update this view
        this.asReadFromModel(dbObj);
        return
    }
    // Something has changed because of an edit in this view
    // so first check if the database has changed under us
    if (!this.equalObjects(dbObj, this.latestModel)) {
        // Later we'll do something more intelligent here...
        console.log(this.itemName + this.seqNo + " **conflict**");
        console.log("   overwriting: " + dbObj.content);
    }
    console.log(this.itemName + this.seqNo + " changed: " + obj.content);
    this.dbStoreModel();
}

