function equalObjects(a, b) {
    // Return true if a and b are 'equal' JS objects
    // [BTW: is there already a way to do this in JS?]
    for (var p in a) if (a[p] != b[p]) return false
    return true;
}

218.source:
function dbStoreModel() {  // this.dbStoreModel()
    // Store the model for this view in the database
    var db = this.dbGetDB();
    this.timeChanged = new Date(); 
    var updatedModel = this.asModel();
    db[this.timeStamp] = updatedModel;
    this.latestModel = updatedModel;
    return;
}

