230.source:
function asModel() {  
    // JSON.stringify(this.asModel())

    // Returns a JS object with field data as properties
    // in adition to a few fixed properties, this scans the morph
    // for fields, which are basically widgets that know "getModelData"
    var obj = {itemName: this.itemName,
        timeStamp: this.timeStamp,
        seqNo: this.seqNo,
        authorID: this.authorID,
        timeChanged: this.timeChanged};
    this.submorphs.forEach(function(morph) {
        if (morph.getModelData) obj[morph.name] = morph.getModelData()
    });
    return obj
}

233.source:
function asReadFromModel(obj) {
    this.itemName = obj.itemName;
    this.timeStamp = obj.timeStamp;
    this.seqNo = obj.seqNo;
    this.authorID = obj.authorID;
    this.timeChanged = obj.timeChanged;
    this.latestModel = obj;
    this.submorphs.forEach(function(morph) {
        if (morph.setModelData) morph.setModelData(obj[morph.name]);
    });
    return this
}

