246.source:
function openObject(obj) {
    var view = this.views[obj.itemName];

    // Do not open the master view (use openView for this)
    if (obj.timeStamp == view.timeStamp) return null;

    var m = view.copy();
    m.asReadFromModel(obj);
    m.setFill(Color.blue.lighter().lighter())
    m.openInWorld();
    return m;
}

