function testTwoViews() {  // this.testTwoViews()
    // Open two views on the same data object for testing
    for (var p in this.DB) {
        var m = this.openObject(this.DB[p]);
        if (m) {
            // if success in opening, open a second and return
            this.openObject(this.DB[p])
            return
        }
    }
}
