function setMethodName(aMethodName) {
    this.methodName = aMethodName;
    if(!aMethodName) {this.codePane.setTextString(''); return; }
    var method = this.selectedClass.prototype[aMethodName];
    this.codePane.setTextString(method.toString());
}

