function setSelectedClass(aClass) {
    this.selectedClass = aClass;
    this.methodPane.setList(aClass ? aClass.functionNames().sort() : []);
    this.owner.setTitle(aClass ? aClass.displayName : "Simple Browser");
    this.codePane.doitContext = aClass ? aClass.prototype : Global;
}

