function doBrowse() {
    module('lively.Helper').load(true);
    module('apps.XMLPrettifier').load(true);

    var browser = this.owner;

    var baseURL = this.get('serviceURL').textString;
    var query = this.get('queryField').textString.trim();

    if (this.get('browseQuery').isChecked() &&
        (query != '')) {

        var s = new Sys.Data.OpenDataServiceProxy(baseURL);
        withLayers([Microsoft2LKLayer], function() {
            s.query(
                '/' + query,
                browser.onSuccess.bind(browser),
                browser.onFailure.bind(browser)
            );
        });
    } else {
        var url = new URL(baseURL + '/$metadata');
        var xml = new WebResource(url).get().content;
        var xmlDOM = new DOMParser().parseFromString(xml, "text/xml").documentElement;

        var conv = new lively.Helper.XMLConverter();
        var json = conv.convertToJSON(xmlDOM);
        edmx = XMLJSONPrettifier.makePretty(json);

        function cleanMetadata(obj) {
            Properties.own(obj).each(function(prop) {
                if (prop.startsWith('_'))
                    delete obj[prop];
                else if (typeof obj == 'object')
                    cleanMetadata(obj[prop]);
            });
        }

        cleanMetadata(edmx);

        var explorer = this.world().openPartItem('Explorer', 'PartsBin/Tools');
        explorer.explore(edmx, 'Metadata');
    }
}

