function collectDeclarations() {
    var morphName = this.get('sceneRoot').textString,
        morph;
    try {
        morph = eval(morphName);
    } catch(e) {
        this.displayDeclarations([]); return;
    }
    if (!morph) { this.displayDeclarations([]); return;};
    var dataForms = [];
    morph.withAllSubmorphsDo(function(ea) {
        if (ea.isDataForm) {
            dataForms.push(ea);}});
    this.displayDeclarations(dataForms);
}

