function createDeclarationFor(aMorph) {
    var dataFields = [],
        result = {
            name: aMorph.getName(),
            dataFields: []};
    aMorph.withAllSubmorphsDo(function(ea) {
        if (ea.isDataField) {
            dataFields.push(ea);}});
    dataFields.forEach(function(ea) {
        var field = {};
        field.name = ea.getName();
        field.type = ea.getType();
        field.partName = ea.partsBinMetaInfo.partName;
        field.partsSpaceName = ea.partsBinMetaInfo.partsSpaceName;
        result.dataFields.push(field);
    });
    return result;
}

