    return this.getQueryString() + ' ' +
        this.createFilterString() +
        ' limit ' + this.getNumRows() + ' offset ' + this.scrollOffset;
}

1327.source:
function createFilterString() {
    if (!this.filterGrid) {
        return;
    }
    var filters = this.filterGrid.submorphs.select(function(ea) {
        return ea.isFilter;});
    var res = ' where ';
    filters.forEach(function(ea) {
        var sql = ea.asSql();
        if (sql != '') {
            res += sql + ' AND ';
        }
    });
    res += '1=1 ';
    return res;
}

