1339.source:
function createGrid() {
    if (!this.getTableName()) {
        return;
    }
    this.removeGrid();
    this.grid = new lively.morphic.DataGrid(1,6);
    this.grid.setName('Grid');
    var structure = [{name: 'rowid', type: 'int'}];
    structure.pushAll(this.get('DatabaseConnector').
            tableStructure(this.getTableName()));
    for (var i = 1; i < structure.length; i++) {
        this.grid.addCol();
    }
    this.grid.setColNames(structure.map(function(ea) { return ea.name;}));
    this.grid.setPosition(pt(10,90));
    this.grid.setExtent(pt(450,200));
    var colWidth = 450/structure.length;
    this.grid.colHeads.forEach(function(ea) {
        ea.setExtent(pt(colWidth, ea.getExtent().y))});
    this.addMorph(this.grid);
    connect(this.grid, 'activeCellContent', this, 'onCellChanged')

}

