1355.source:
function createFilterGrid() {
    this.removeFilterGrid();
    var structure = this.get('DatabaseConnector').
            tableStructure(this.getTableName());  

    var filterGrid = new lively.morphic.Morph();
    filterGrid.setFill(Color.white);
    filterGrid.setExtent(pt(300, structure.length*20));
    filterGrid.setPosition(pt(10, 310));

    var col = 0;
    structure.forEach(function(ea) {
        var label = new lively.morphic.Text();
        label.setExtent(pt(100,20));
        label.gridCoords = pt(0, col);
        label.setBorderWidth(0);
        label.textString = ea.name;

        var filter = new lively.morphic.Text();
        filter.setExtent(pt(100,20));
        filter.setFill(Color.rgb(251,255,91));
        filter.setBorderWidth(0);
        filter.gridCoords = pt(1, col);
        filter.name = ea.name;
        filter.isFilter = true;
        if (ea.type == 'string' || ea.type == 'text') {
            filter.addScript(function asSql() {
                return this.name + " like '" + this.textString + "%'";
            });
        } else {
            filter.addScript(function asSql() {
                if (this.textString == '') {
                    return '';
                }
                return this.name + " = "  + this.textString;
            });
        }
        filter.addScript(function onEnterPressed() {
            this.owner.owner.updateTable();
        });

        filterGrid.addMorph(label);
        filterGrid.addMorph(filter);
        col++;
    });
    filterGrid.setLayouter(new lively.morphic.Layout.GridLayout(filterGrid, 2, col));
    filterGrid.applyLayout();

    this.addMorph(filterGrid);
    this.filterGrid = filterGrid;
}

1371.source:
function updateTable() {
    var dbc = this.get('DatabaseConnector');

    var tmp = [];
    if (dbc.tableExists('metadata')) {
        tmp = dbc.fetchAll("SELECT * FROM metadata WHERE tableName like '" + this.getTableName() + "'");
    }
    var metadata = null;
    if (tmp.length > 0) {
        metadata = {};
        tmp.forEach(function(ea) {
            metadata[ea.columnName] = ea.selector;
        });
    }
    var queryString = this.createQueryString(),
        data = dbc.fetchAll(queryString);

    if (data[0]) {
        var fields = [];
        for (var field in data[0]) {
            fields.push(field); 
        }
        this.get('Grid').setColNames(fields);
    }

    var displayData = [];
    data.forEach(function(ea) {
        var curObj = {};
        for (var field in ea) {
            var curField = ea[field];

            if (metadata && metadata[field]) {
                var foreignTable = metadata[field].split('.')[0];
                var foreignCol = metadata[field].split('.')[1];
                var tmpField = dbc.fetchAll(
                    'select ' + foreignCol + ' from ' + 
                    foreignTable + ' where rowid = ' + data[0][field]);
                if (tmpField && tmpField[0] && tmpField[0][foreignCol]) {
                    curField = tmpField[0][foreignCol];
                    curField.__secretHiddenValue = ea[field]; // see DataReference>>setValue
                }
            }
            curObj[field] = curField;
        }
        displayData.push(curObj);
    });

    this.get('Grid').setData(displayData);
}

