function setupTable(aString) {
    this.stopStepping();
    this.tableName = aString;
    if (this.get('tableName').textString != aString) {
        this.get('tableName').textString = aString;
    }
    this.setQueryString('SELECT rowid,* FROM ' + aString);
    this.createGrid();
    this.createFilterGrid();
    this.updateTable();
    this.startStepping(1000, 'updateTable');
}

