function onSelectionChanged(anObject) {
    var prevSelection = this.selection;

    if (!anObject) {
        return;
    }
    if (!prevSelection) {
        this.selection = anObject;
        return;
    }
    for (var prop in anObject) {
        if (anObject[prop] != prevSelection[prop]) {
            this.selection = anObject;
            return;
        }
    }

}
