function createListMorph() {
    var list = this.getList();
    if (!list) return;  

    var listMorph = new lively.morphic.List(new Rectangle(0,0, this.getExtent().x, list.length * 17), list);

    listMorph.disableGrabbing();
    listMorph.disableDragging();

    connect(listMorph, 'selection', this, 'onSelection');

    return listMorph;
}

