function getList() {

    if (!this.owner.getSelectorString()) {
        alert('Must set selector string first (Halo M)');
        return;
    }

    var tableName = this.owner.parseSelectorString().table,
        columnName = this.owner.parseSelectorString().col,
        dbc = this.get("DatabaseConnector");
    var rows = dbc.fetchAll('SELECT *,rowid FROM ' + tableName);

    var options = rows.map(function (each) {
        return {
            isListItem: true,
            selectionString: each[columnName].toString(),
            string: each[columnName].toString(),
            value: each
        }
    });

    return options;
}

