function onBlur(evt) {

    $super(evt);

    // workaround - otherwise other morphs get this event
    var clickedMorph = evt && evt.world && evt.world.clickedOnMorph;
    if (clickedMorph && (clickedMorph !== this && clickedMorph !== this.listMorph)) {
        this.removeList();
    }
}

