function reset() {
    this.list = [];
    this.setList([]);
    this.label.textString=new String();
    disconnectAll(this);
    this.selection = null;
    connect(this, "selection", this, "onSelectionChanged");
    connect(this.label, "savedTextString", this, "selection", {});
}

