function onMouseDown(evt) {
    if (evt.isCommandKey() || evt.isRightMouseButtonDown()) return $super(evt);
	this.listMorph
    if (this.listMorph) {
    if (evt.target === this.renderContext().shapeNode)
        this.removeList();
    } else {
        var list = this.createListMorph();
        this.bringToFront();
        if (!list) return;
        this.addMorph(list);
        list.align(list.bounds().topLeft(), 
        this.shape.bounds().bottomLeft())
        this.listMorph = list;
    }
    return true;
}

