function setValue(value) {
    if (value.constructor.name === 'Object') {
        this.value = value.rowid;
        var previewCol = this.parseSelectorString().col;
        return this.get('Button').setLabel(value[previewCol]);
    } else {
        this.get('Button').setLabel('[' + value + ']');
    }
}

260.source:
function parseSelectorString() {
    var split = this.getSelectorString().split('.');
    return {
        table: split[0],
        col: split[1]
    }
}

