function morphMenuItems() {
    var items = $super();
    var that = this;
    items.push(['set selector',
        function(evt) {
            $world.prompt('Set selector', function(input) {
                that.setSelectorString(input || '');
            }, that.getSelectorString() || '');
        }]);
    return items;
}

