104.source:
function toggleRun() {
    if (this.isRunning) this.stop()
    else this.run();
}

107.source:
function run() {
    this.isRunning = true;
    this.startStepping(200, 'updateInspection')
    this.get('runButton').setLabel('stop')
}

110.source:
function stop() {
    this.isRunning = false;
    this.stopStepping()
    this.get('runButton').setLabel('run')
}

116.source:
function updateInspection() {
    var name = this.get('curveName').textString,
        m = $morph(name);
    if (!m || !m.isPath) {
        alert('cannot find ' + name);
        this.stop();
    }
    newShowPt(m.worldPoint(pt(0,0)), 0.1)
    var str = ''
    str += 'morph pos: ' + m.getPosition() + '\n';
    str += 'morph origin: ' + m.getOrigin() + '\n';
    str += 'bounds: ' + m.bounds() + '\n';
    str += 'shape pos: ' + m.shape.getPosition() + '\n';
    str += '\n';
    str += 'viewBox: ' + m.renderContext().shapeNode.getAttribute('viewBox') + '\n';
    str += m.shape.getPathElements().join(' ');
    this.get('inspectionOutput').setTextString(str)
}
