function startTrace() {
    alertOK('start trace')
    this.installTracer();
    // cop.Tracing.ObjectTraceLayer.beGlobal();
    this.get('traceResult').textString = ''
    this.get('TracingArea').setWithLayers([cop.Tracing.ObjectTraceLayer])
}

