function showResults() {
    // show the result
    if (!this.tracer) {alertOK("nothing to stop"); return};

    this.tracer.show(function(frame, depth) {
        var style = {color: Color.black}
        if (frame.obj.getFill && frame.obj.getFill() instanceof Color)
            style.color = frame.obj.getFill();
        // if (frame.obj.name == 'morph')
            // style.size = 18;
        return style
    }, this.get('traceResult'));
    this.tracer = null;
}

