function installTracer() {
    module('cop.Tracing').load(true);

    this.tracer = cop.Tracing.ObjectTracer.reset();
    this.tracer.recordTime = true;

    // set classes to trace
    var klasses = this.get('traceClasses').evalAll();
    cop.Tracing.ObjectTracer.instrument(klasses);

    // set ignore props
    this.tracer.ignoreList = this.tracer.ignoreList
        .concat(this.get('ignoreProperties').evalAll())

}
