function createRow(y, object, propertyData){
	var aLabel = new lively.morphic.Text(new Rectangle(this.innerBounds().x,y,this.colEvenWidth-this.cellSpacing,this.rowEvenHeight));
	aLabel.applyStyle(this.labelStyleSpec);
	aLabel.beLabel(); 
	aLabel.textString = propertyData.isOwn?propertyData.name:propertyData.name+" (p)";
	var aValue = new lively.morphic.Text(new Rectangle(this.innerBounds().x+this.colEvenWidth,y,this.colEvenWidth-this.cellSpacing,this.rowEvenHeight));
	aValue.propertyData = propertyData;
	aValue.textString = propertyData.valueDisplay;
	connect(aValue.propertyData, 'value', aValue.propertyData, 'valueDisplay');//changes in value are reflected in valueDisplay
        connect(aValue.propertyData, 'value', this.targetObject, aValue.propertyData.name);
	connect(aValue.propertyData, 'valueDisplay', aValue, 'textString'); //changes in valueDisplay are reflected visually
	// aValue.applyStyle(this.labelStyleSpec);
	if(propertyData.editor){ 
		aValue.applyStyle({borderWidth:1,borderColor:new Color(1,1,1,1), strokeOpacity:1, fillOpacity:0, fixedHeight:true, fixedWidth:true, fill:new Color(1,1,1,1)}); 
		aValue.onMouseDown = function(evt){
			try{
				var psCopy = this.get('PropertySheet').copy();
				psCopy.reset();
				psCopy.targetObject = propertyData.value;
				psCopy.ownerObject = aValue;
				psCopy.ownerObjectPropertyNameForThis = propertyData.name;
				psCopy.createTable(psCopy.targetObject);
				psCopy.openInWorld(this.get('PropertySheet').getPosition().addXY(50,50), 'Property Sheet - ' + propertyData.name);
				psCopy.name='Property Sheet - ' + propertyData.name;

			} catch(e){console.warn('Error caught while opening property sheet for object ' + propertyData.value);}
			evt.stop();
		}
		aValue.registerForEvent('onMouseDown',aValue, 'onMouseDown', true);	
                aValue.allowInput = false;
		/*only simple types editable. on next step objects will be assigned editors/validators*/
		//aValue.beLabel();
	} else {
		// aValue.beInputLine();	
		aValue.applyStyle({borderWidth:1, strokeOpacity:0, fillOpacity:1, fixedHeight:true, fixedWidth:true, fill:new Color(1,1,1,1)});
                self = this;
		connect(aValue, 'textString', aValue.propertyData, 'value', {varMapping: {val:aValue.propertyData, ctx:self},       
                                converter:function(newValue, oldValue){
                                    var _v = ctx.parseString(newValue,val.value);
                                    if(_v) return _v;
                                    else return newValue;
                                }
                });//changes in inputField change the property value data.
		// connect(aValue, 'textString', aValue.propertyData, 'valueDisplay');//changes in inputField change the property value display.
		if(this.ownerObject){
			connect(aValue.propertyData, 'value', this.ownerObject.propertyData.value, aValue.propertyData.name); 
			//Don't know how to trigger update of the valueDisplay as well. I tried to reference it from the updated functon definition provided to connection but you basically have access to nothing but src and target there.
			if(aValue.propertyData.value !== this.ownerObject.owner.targetObject[this.ownerObjectPropertyNameForThis][aValue.propertyData.name]){ /*This never happens actually because the connect above modifies the propertyData value object which happens to be a reference to the targetObject's value of that property. Hence, the change commes immediately into effect.*/
				aValue.textString = aValue.propertyData.valueDisplay;//show changed value and not the original here
				aValue.toggleItalics(0,aValue.textString.length);
			}
		}
	} 
	aValue.setClipMode('hidden');
	aValue.setFontSize(10);
	return {label:aLabel, value:aValue};
}

