function createTable(object){
        this.targetObject = object;

	var tableHeader = new lively.morphic.Text(new Rectangle(this.innerBounds().x, this.innerBounds().y, this.getExtent().x, this['rowEvenHeight']));

	tableHeader.textString = (this["targetObject"]["getName"] && this["targetObject"]["getName"]()) || (this["targetObject"]["id"] && this["targetObject"].id) || "unidentified object";
	tableHeader.beLabel();
        tableHeader.applyStyle({ align: 'center',
			         borderWidth: 1,
			         borderColor: Color.gray.darker(),
			         fill: Color.gray.lighter(),
            			 clipMode: "hidden",
            			 fillOpacity: 0,
			         fixedHeight: false,
			         fixedWidth: false,
                                 fontFamily: 'Arial, Verdana',
			         fontSize: 10,
			         padding: new Rectangle(0,0,2,2),
			         verticalAlign: "middle"});
	tableHeader.toggleBoldness(0,tableHeader.textString.length);
        this.addMorph(tableHeader);
	this.createHeadingRow();
	var y = this.rowEvenHeight*2;
	var data = this.getTableData(object,true);
	for(i=0;i<data.length;i++){
		y += this.cellSpacing;
		var row = this.createRow(y,object,data[i]);
		this.addMorph(row.label);
		this.addMorph(row.value); 
		y+=this.rowEvenHeight;			
	}
	// this.setExtent(this.getExtent().addXY(0,this.cellSpacing + y - this.getExtent().y));
}

