function getTableData(object, sortDesc){
	var data = [];
        self = this;
	Properties.all(object,this.acceptProperty).each(function(p) {
		var row = {
			name: p,
			isOwn: object.hasOwnProperty(p)?true:false,
			valueDisplay: (typeof object[p] === 'object' && (object[p] != null) && object[p].constructor.type)?object[p].constructor.type:object[p],
			editor: (typeof object[p] === 'object')? this: undefined,
			value: object[p]
		};
		data.push(row);
	});
 	return (sortDesc)? data.sort(function(a,b) {return a.name>b.name ? 1 : a.name<b.name ? -1 : 0;}) : data.sort(function(a,b) {return a.name<b.name ? 1 : a.name>b.name ? -1 : 0;});
}

