function acceptProperty(name, object) {
    var isAccepted = false;
    if(!self.accepts || !self.accepts.length){ //No accepts filter
        isAccepted = true;
    }else{
        self.accepts._each(function(value,index){
                if(name === value){
                    isAccepted = true;
                } 
        });
    }
    var isDenied = false;
    if(self.denies || self.denies.length){ //Denies filter exist
        self.denies._each(function(value,index){
                if(name === value){
                    isDenied = true;
                } 
        });
    } 
    return isAccepted && !isDenied;
}

