function subscribe() {
	lively.bindings.connect(lively.morphic.World.current(),"selection", this, "targetObject", {updater:function($upd){
                    target['setObject'](source['selection']);
                    $upd();
                }});

        lively.bindings.connect(lively.morphic.World.current(),"clickedOnMorph", this,   "targetObject", {updater:function($upd){
                    if(source["clickedOnMorph"] && source["clickedOnMorph"]["showsHalos"] && source["clickedOnMorph"] !== target["targetObject"]){
                         target['setObject'](source['clickedOnMorph']);
                        $upd();
                    }
                }});

}
