function showTextBounds(m) {
    if (m.isEpiMorph) return
    if (m.textChunks) {
        m.textChunks.each(function(ea) {
            this.showClientRect(ea.chunkNode.getBoundingClientRect())
            $A(ea.chunkNode.getClientRects()).forEach(function(r) {
                this.showClientRect(r)
            }, this)
        }, this)
    }
}

202.source:
function showClientRect(r) {
    var b = new Rectangle(r.left, r.top, r.width, r.height)
    var m = showRect(b, 5)
    m.isEpiMorph = true
    m.disableEvents()
    m.setBorderColor(Color.green)
    m.setFill(Color.green.withA(0.3))
}

