function setupConnections() {
    connect(this.get('selectMorphButton'), 'fire', this, 'findAndConnectMorph');
    connect(this.get('updateButton'), 'fire', this, 'update');
    connect(this.get('clearButton'), 'fire', this, 'reset');
    connect(this.get('chunkList'), 'selection', this, 'showTextChunk');
}

