function findAndConnectMorph(morph) {
    if (morph) {
        this.get('morphNameText').textString = morph.getName();
    }
    if (!morph) {
        var name = this.get('morphNameText').textString;
        morph = $morph(name);
    }

    if (!morph || !morph.isText) {
        alert(morph + ' is not a text morph, cannot observe!')
        return;
    }

    if (this.selectedMorph) this.disconnectSelectedMorph();

    this.selectedMorph = morph;

    this.startStepping(500, 'update');

    alertOK('Connected to ' + morph);
    lively.morphic.show(morph);
}

