function update() {
    var morph = this.selectedMorph;
    if (!morph) {
        this.reset();
        return;
    }
    var s = morph.textString;

    // textString
    this.get('txt1').textString = s;

    // chars
    this.get('txt2').textString = s.length.toString()
                                + " "
                                + s.toArray().join(',');

    // char codes
    this.get('txt3').textString = s.length.toString()
                                + " "
                                + s.toArray().collect(function(e) { return e.charCodeAt(0); }) 

    // textNode
    if (this.get('txt4').textString !== morph.textNodeString()) {
        this.get('txt4').textString = morph.textNodeString();
    };

    // text chunks
    var chunks = morph.getTextChunks().clone(),
        chunksInList = this.get('chunkList').itemList.collect(function(ea) {
            return ea.value; });
    if (!chunks.equals(chunksInList)) {
        var items = chunks.collect(function(chunk) {
            return {isListItem: true, string: String(chunk), value: chunk};
        });
        this.get('chunkList').setList(items);
    }

    var otherState = '';
    // selection
    otherState += 'selection range: ' + morph.getSelectionRange();

    // syntax highlighting
    otherState += ', time for last syntax highlighting: ' + this.printLastSyntaxHighlightingTimes(morph);

    this.get('txt6').textString = otherState;

    // chunk age
    this.shouldShowChunkAge() && this.showChunkAge(morph);

}

