function reset() {
    this.stopStepping();
    this.disconnectSelectedMorph();
    this.get("morphNameText").textString = "No morph selected";
    this.applyStyle({adjustForNewBounds: true});
    this.get("morphNameText").applyStyle({fontSize: 10, padding: Rectangle.inset(2)});
    Array.range(1, 6).forEach(function (n) {
        var name = "txt" + n, text = this.get(name);
        text.setTextString("");
        text.applyStyle({
            fontSize: 8, scaleProportional: false, clipMode: "auto", padding: Rectangle.inset(2,2)
        });
    }, this);
    var chunkList = this.get("chunkList");
    chunkList.doNotSerialize = ["itemList", "selection"];
    chunkList.setList([]);
    chunkList.setSelection(null);
    chunkList.applyStyle({fontSize: 8, scaleProportional: true});
    this.lastSyntaxHighlightTimes = [];
}

