function showTextChunk(chunk) {
    if (!chunk) {
        this.get('txt5').textString = '';
        return;
    };
    this.get('txt5').textString = chunk.toString()
                                + '\n\n'
                                + JSON.prettyPrint(chunk.toPlainObject());

    // highlight the chunk DOM representation
    // 1. in the text itself
    var node = chunk.getChunkNode(),
        domBounds = node.getBoundingClientRect(),
        bounds = new Rectangle(
            domBounds.left + document.body.scrollLeft, domBounds.top + document.body.scrollTop,
            domBounds.width, domBounds.height);
    show(bounds);

    // 2. in the text DOM string 
    var domString = Exporter.stringify(node),
        textDomStringText = this.get('txt4'),
        domStringStart = textDomStringText.textString.indexOf(domString);
    textDomStringText.unEmphasize(0, textDomStringText.textString.length);
    if (domStringStart < 0) return;
    textDomStringText.emphasize(
        {color: Color.red},
        domStringStart, domStringStart+domString.length);
        textDomStringText.emphasize({})
}

