function showChunkAge(text) {
    var tenSeconds = 10 * 1000,
        chunks = text.getTextChunks();
    chunks.forEach(function(chunk) {
        var t = chunk.creationTime, now = Date.now();
        if (!chunk.creationTime) { chunk.creationTime = now; return; }
        var age = now - t,
            h = Math.min((age / tenSeconds) * 240, 240),
            color = Color.hsb(h, 0.5, 0.8);
        chunk.getChunkNode().style.background = color.toCSSString();
    });
}

