function printLastSyntaxHighlightingTimes(text) {
    if (!text.syntaxHighlightingWhileTyping) return 'not enabled';
    var last = text.lastSyntaxHighlightTime;
    if (last != this.lastSyntaxHighlightTimes.first()) {
        this.lastSyntaxHighlightTimes.unshift(text.lastSyntaxHighlightTime);
        this.lastSyntaxHighlightTimes = this.lastSyntaxHighlightTimes.slice(0,5);
    }
    return String(this.lastSyntaxHighlightTimes);
}
