function onMouseUp(evt) {
	if (evt.isCommandKey() || evt.isRightMouseButtonDown()) return $super(evt);
	if (this.listMorph) { 
		this.removeList(); 		
		return true ;
	}
	var list = this.createListMorph();
	this.addMorph(list);
	list.align(list.bounds().topLeft(), this.shape.bounds().bottomLeft())
	connect(list, 'selection', this, 'selection');
	connect(list, 'selection', this, 'removeList');
	if (this.listMorph) this.listMorph.remove();
	this.listMorph = list;
	return true;
}

